import './header.scss';
import { useState } from 'react';
function Header(){
    const [isLanguageDropdownOpened, setisLanguageDropdownOpened] = useState(false);
    function toggleLanguageDropdown(){
        setisLanguageDropdownOpened(!isLanguageDropdownOpened)
    }
    return (
        <div class="header-container">
            <div class="header-container__logo">
                EasyDest
            </div>
            <div  class="header-container__nav">
                <span onClick={toggleLanguageDropdown} title="Language"><i class="fa fa-language header-container__nav__language" aria-hidden="true"></i></span>
                <span title="Profile"><i class="fa fa-user header-container__nav__profile" aria-hidden="true"></i></span>
                {
                    isLanguageDropdownOpened && (
                        <div class="header-container__nav__language-dropdown">
                            <ul>
                                <li>English</li>
                                <li>বাংলা</li>
                            </ul>
                        </div>
                    )
                }
            </div>

        </div>
    )
}

export default Header;
